import React                from 'react'


import s                    from './ParallaxLayer.css'

class ParallaxLayer extends React.Component {
  render() {
    const {
      children,
      offset,
      size,
      style,
      scrollTop,
      speed = 1,
    } = this.props

    return (
      <div
        className={ s.root }
        style={{
          ...style,
          top: offset,
          height: size,
        }}
      >
        <div style={{
          top: -scrollTop * speed,
        }}>
          { children }
        </div>
      </div>
    )
  }
}

import withStyles           from 'isomorphic-style-loader/lib/withStyles'

export default withStyles(s)(ParallaxLayer)
